import 'package:dio/dio.dart';import 'package:retrofit/retrofit.dart';import '../models/category/all_categories_model.dart';import '../models/location/shops_location.dart';import '../models/product_items/product_items_model.dart';import '../models/slider/slider_model.dart';import '../models/top_categories/top_categories_model.dart';import '../models/top_products/top_products_model.dart';part 'api_service.g.dart';@RestApi()abstract class ApiService {  factory ApiService(Dio dio, {String baseUrl}) = _ApiService;  @GET('web/v1/content/sliders')  Future<SliderModel> getSliders();  @GET('web/v1/category/catalog')  Future<AllCategoriesModel> getAllCategories();  @GET('web/v1/home/special-categories')  Future<TopCategoriesModel> getTopCategories();  @GET('web/v1/home/special-products?type=hit_products')  Future<TopProductsModel> getTopProducts();  @GET('common/v1/search/filters')  Future<ProductItemsModel> getProductItems(@Query('category') String category);  @GET('web/v1/region/stores-list')  Future<ShopsMapModel> getShopsLocation();}