import 'package:flutter/cupertino.dart';import '../../../../data/source/local/hive_helper.dart';class DashboardProvider extends ChangeNotifier {  int count = 0;  DashboardProvider() {    getBasketCount();  }  void getBasketCount() {    count = HiveHelper.getAllProductsFromBasket().length;    notifyListeners();  }  void decrement() {    count--;    notifyListeners();  }  void increment() {    count++;    print('increment: ${count}');    notifyListeners();  }}