import 'package:bloc/bloc.dart';import 'package:meta/meta.dart';import '../../../../data/source/models/location/shops_location.dart';import '../../../../data/source/repository/app_repository.dart';import '../../../../di/di.dart';part 'map_list_event.dart';part 'map_list_state.dart';class MapListBloc extends Bloc<MapListEvent, MapListState> {  final _repository = getIt<AppRepository>();  MapListBloc() : super(MapListInitial()) {    on<GetAllMapListEvent>((event, emit) async {      emit(LoadingState());      final result = await _repository.getLocation();      emit(GetAllMapListState(result));    });  }}