import 'package:bloc/bloc.dart';import 'package:meta/meta.dart';import '../../../../data/source/models/location/shops_location.dart';import '../../../../data/source/repository/app_repository.dart';import '../../../../di/di.dart';part 'shops_location_event.dart';part 'shops_location_state.dart';class ShopsLocationBloc extends Bloc<ShopsLocationEvent, ShopsLocationState> {  final _repository = getIt<AppRepository>();  ShopsLocationBloc() : super(ShopsLocationInitial()) {    on<GetAllShopsEvent>((event, emit) async {      final data = await _repository.getLocation();      emit(GetAllShopsState(data));    });  }}