import 'package:flutter_texnomart/utils/product_holder.dart';import 'package:hive/hive.dart';class ProductItemsModelAdapter extends TypeAdapter<ProductHolder> {  @override  ProductHolder read(BinaryReader reader) {    return ProductHolder.fromJson(reader.readMap().cast());  }  @override  int get typeId => 0;  @override  void write(BinaryWriter writer, ProductHolder obj) {    writer.writeMap(obj.toJson());  }}